mapper.select:
select 
from discuss_post
where status != 2

    and user_id = #{userId}

order by type desc, create_time desc
limit #{offset}, #{limit}

select 
from discuss_post
where status != 2 and kind = #{kind}
order by type desc, create_time desc
limit #{offset}, #{limit}

select count(id)
from discuss_post
where status != 2 and kind = #{kind}
